import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';

function RecipesDoneAndFavorite({ location: { pathname } }) {
  const { done, favorite } = useSelector((state) => state.recipes);
  const [recipes, setRecipes] = useState();

  useEffect(() => {
    if (pathname.endsWith('feitas')) setRecipes(done);
    else setRecipes(favorite);
  }, [pathname]);

  return (
    <div>
      <button
        type="button"
        data-testid="filter-by-all-btn"
      >
        All
      </button>
      <button
        type="button"
        data-testid="filter-by-food-btn"
      >
        Food
      </button>
      <button
        type="button"
        data-testid="filter-by-drink-btn"
      >
        Drinks
      </button>
      {recipes.map((recipe, index) => (
        <div key={ recipe.id }>
          <img src={ recipes.image } alt={ recipe.name } data-testid={`${index}-horizontal-image`} />
        </div>
      ))}
    </div>

  );
}

export default RecipesDoneAndFavorite;
