import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { useSelector } from 'react-redux';
import ShareButton from '../components/ShareButton';

function RecipesDoneAndFavorite({ location: { pathname } }) {
  const { done, favorite } = useSelector((state) => state.recipes);
  const [recipes, setRecipes] = useState([]);
  const [isDone, setIsDone] = useState();
  const [filter, setFilter] = useState('todos');

  useEffect(() => {
    if (pathname.endsWith('feitas')) {
      setRecipes(done);
      setIsDone(true);
    } else {
      setIsDone(false);
      setRecipes(favorite);
    }
  }, [pathname]);

  return (
    <div>
      <button
        type="button"
        data-testid="filter-by-all-btn"
        onClick={ () => setFilter('todos') }
      >
        All
      </button>
      <button
        type="button"
        data-testid="filter-by-food-btn"
        onClick={ () => setFilter('comida') }
      >
        Food
      </button>
      <button
        type="button"
        data-testid="filter-by-drink-btn"
        onClick={ () => setFilter('bebida') }
      >
        Drinks
      </button>
      {recipes.map((recipe, index) => (
        <div key={ recipe.id }>
          <img
            src={ recipe.image }
            alt={ recipe.name }
            data-testid={ `${index}-horizontal-image` }
          />
          <p
            data-testid={ `${index}-horizontal-top-text` }
          >
            { recipe.type.startsWith('comida') && `${recipe.area} - ${recipe.category}` }
            { recipe.type.startsWith('bebida') && recipe.alcoholicOrNot }
          </p>
          <h1 data-testid={ `${index}-horizontal-name` }>{ recipe.name }</h1>
          { isDone && (
            <p data-testid={ `${index}-horizontal-done-date` }>
              {recipe.doneDate}
            </p>
          )}
          <ShareButton id={ recipe.id } index={ index } type={ `${recipe.type}s` } />
          {isDone && (
            recipe.tags.map((tag, tagIndex) => (
              <p
                key={ `${recipe.id}-tag-${tagIndex}` }
                data-testid={ `${index}-${tag}-horizontal-tag` }
              >
                { tag }
              </p>
            ))
          )}
        </div>
      ))}
    </div>

  );
}

RecipesDoneAndFavorite.propTypes = {
  location: PropTypes.shape({
    pathname: PropTypes.string.isRequired,
  }).isRequired,
};

export default RecipesDoneAndFavorite;
